def insert_description(kml_path: str, description: str) -> None:
    with open(kml_path, "r", encoding="utf-8") as f:
        content = f.read()

    html_desc = text_to_html(description.format(src=SOURCE_URL))
    new_desc = f"<description><![CDATA[{html_desc}]]></description>"
    updated = re.sub(
        r"<description><!\[CDATA\[.*?\]\]></description>",
        new_desc,
        content,
        count=1,
        flags=re.DOTALL,
    )

    if updated == content:
        print(f"  WARNING: No description tag replaced in {os.path.basename(kml_path)}")
    else:
        with open(kml_path, "w", encoding="utf-8") as f:
            f.write(updated)
        print(f"  OK: {os.path.basename(kml_path)}")


def main():
    print(f"Processing KML files in: {ROUTES_DIR}")
    for filename, description in DESCRIPTIONS.items():
        kml_path = os.path.join(ROUTES_DIR, filename)
        if not os.path.exists(kml_path):
            print(f"  MISSING: {filename}")
            continue
        insert_description(kml_path, description)
    print("Done.")


if __name__ == "__main__":
    main()
